import { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const [usuario, setUsuario] = useState('');
  const [clave, setClave] = useState('');
  const navigate = useNavigate();

  const handleLogin = (e) => {
    e.preventDefault();

    if (usuario === 'estudiante' && clave === '123') {
      localStorage.setItem('usuario', 'estudiante');
      navigate('/est');
    } else if (usuario === 'admin' && clave === '123') {
      localStorage.setItem('usuario', 'admin');
      navigate('/coord');
    } else {
      alert('Credenciales incorrectas');
    }
  };

  return (
    <div className="container mt-5">
      <h2>Login</h2>
      <form onSubmit={handleLogin}>
        <input className="form-control my-2" type="text" placeholder="Usuario" onChange={e => setUsuario(e.target.value)} />
        <input className="form-control my-2" type="password" placeholder="Contraseña" onChange={e => setClave(e.target.value)} />
        <button className="btn btn-primary" type="submit">Iniciar sesión</button>
      </form>
    </div>
  );
};

export default Login;
