import React, { useState } from 'react';
import { Button, Form, Card } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';

const AgendarFicha = () => {
  const [ficha, setFicha] = useState({
    paciente: '',
    estudiante: '',
    fecha_registro: '',
    numero_atencion: '',
    tiene_diagnostico: false
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFicha(prev => ({
      ...prev,
      [name]: name === 'tiene_diagnostico' ? value === 'true' : value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Ficha registrada:', ficha);
    // Aquí podrías guardar en localStorage o enviar al backend
    navigate('/fichas');
  };

  return (
    <Card className="p-4 mt-4">
      <h4>Agendar Nueva Ficha</h4>
      <Form onSubmit={handleSubmit} className="mt-3">
        <Form.Group className="mb-3">
          <Form.Label>Paciente</Form.Label>
          <Form.Control name="paciente" value={ficha.paciente} onChange={handleChange} required />
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>Estudiante</Form.Label>
          <Form.Control name="estudiante" value={ficha.estudiante} onChange={handleChange} required />
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>Fecha de Registro</Form.Label>
          <Form.Control type="date" name="fecha_registro" value={ficha.fecha_registro} onChange={handleChange} required />
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>Número de Atención</Form.Label>
          <Form.Control type="number" name="numero_atencion" value={ficha.numero_atencion} onChange={handleChange} required />
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>Diagnóstico Asociado</Form.Label>
          <Form.Select name="tiene_diagnostico" value={ficha.tiene_diagnostico} onChange={handleChange}>
            <option value="false">Sin diagnóstico</option>
            <option value="true">Tiene diagnóstico</option>
          </Form.Select>
        </Form.Group>

        <div className="d-flex justify-content-end gap-2">
          <Button variant="secondary" onClick={() => navigate('/fichas')}>Cancelar</Button>
          <Button type="submit" variant="success">Guardar Ficha</Button>
        </div>
      </Form>
    </Card>
  );
};

export default AgendarFicha;
