import React, { useRef, useState } from 'react';
import { Form, Button, Row, Col } from 'react-bootstrap';
import './AgendarFicha.css';

const totalSteps = 4;

const AgendarFicha = () => {
  const [step, setStep] = useState(1);
  const [formData, setFormData] = useState({});
  const containerRef = useRef(null);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const scrollToTop = () => {
    containerRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const validateStep = () => {
    if (step === 1) {
      return formData.nombre_paciente && formData.apellido_paciente;
    }
    return true;
  };

  const nextStep = () => {
    if (step < totalSteps && validateStep()) {
      setStep(step + 1);
      scrollToTop();
    } else if (!validateStep()) {
      alert("Completa los campos requeridos antes de continuar.");
    }
  };

  const prevStep = () => {
    if (step > 1) {
      setStep(step - 1);
      scrollToTop();
    }
  };

  const handleSubmit = () => {
    console.log('Datos enviados:', formData);
    alert('Ficha registrada con éxito.');
  };

  return (
    <div className="container mt-4" ref={containerRef}>
      <h4 className="mb-3">Registro de Ficha Clínica</h4>

      {/* Wizard steps */}
      <div className="wizard-steps mb-4">
        {[1, 2, 3, 4].map((s) => (
          <div key={s} className={`step-circle ${step === s ? 'active' : step > s ? 'done' : ''}`}>
            {s}
          </div>
        ))}
      </div>

      <div className="form-steps-wrapper">
        <div className="form-steps" style={{ transform: `translateX(-${(step - 1) * 100}%)` }}>
          {/* Paso 1 */}
          <div className="form-step">
            <h5>Paso 1: Datos del Paciente</h5>
            <Form>
              <Row>
                <Col md={6}><Form.Group><Form.Label>Nombre del paciente *</Form.Label><Form.Control name="nombre_paciente" value={formData.nombre_paciente || ''} onChange={handleChange} /></Form.Group></Col>
                
              </Row>
            </Form>
          </div>

          {/* Paso 2 */}
          <div className="form-step">
            <h5>Paso 2: Antecedentes Médicos</h5>
            <Form.Group className="mb-3"><Form.Label>Motivo de consulta</Form.Label><Form.Control as="textarea" rows={3} name="motivo_consulta" onChange={handleChange} /></Form.Group>
          </div>

          {/* Paso 3 */}
          <div className="form-step">
            <h5>Paso 3: Evaluación Fisioterapéutica</h5>
            <Form.Group className="mb-3"><Form.Label>Evaluación de la postura</Form.Label><Form.Control as="textarea" rows={2} name="evaluacion_postura" onChange={handleChange} /></Form.Group>
          </div>

          {/* Paso 4 */}
          <div className="form-step">
            <h5>Paso 4: Diagnóstico y Plan de Intervención</h5>
            <Form.Group className="mb-3"><Form.Label>Diagnóstico fisioterapéutico</Form.Label><Form.Control as="textarea" rows={2} name="diagnostico_fisioterapeutico" onChange={handleChange} /></Form.Group>
          </div>
        </div>
      </div>

      {/* Botones */}
      <div className="d-flex justify-content-between mt-4">
        <Button disabled={step === 1} onClick={prevStep}>Anterior</Button>
        {step < totalSteps ? (
          <Button onClick={nextStep}>Siguiente</Button>
        ) : (
          <Button variant="success" onClick={handleSubmit}>Finalizar</Button>
        )}
      </div>
    </div>
  );
};

export default AgendarFicha;
