import React, { useState } from 'react';
import { Button, Form, Card } from 'react-bootstrap';
import './AgendarFicha.css'; // Para animaciones

const AgendarFicha = () => {
  const [step, setStep] = useState(0);
  const [formData, setFormData] = useState({
    paciente: '',
    edad: '',
    motivo_consulta: '',
    evaluacion: '',
    diagnostico: ''
  });

  const handleChange = (e) => {
    setFormData(prev => ({
      ...prev,
      [e.target.name]: e.target.value
    }));
  };

  const nextStep = () => setStep(step + 1);
  const prevStep = () => setStep(step - 1);

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Ficha registrada:', formData);
  };

  return (
    <Card className="p-4 mt-4 slide-form-container">
      <h4>Registro de Ficha Clínica</h4>
      <div className={`slide-form step-${step}`}>
        {step === 0 && (
          <div className="form-step">
            <Form.Group className="mb-3">
              <Form.Label>Paciente</Form.Label>
              <Form.Control name="paciente" value={formData.paciente} onChange={handleChange} />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Edad</Form.Label>
              <Form.Control name="edad" value={formData.edad} onChange={handleChange} />
            </Form.Group>
          </div>
        )}

        {step === 1 && (
          <div className="form-step">
            <Form.Group className="mb-3">
              <Form.Label>Motivo de consulta</Form.Label>
              <Form.Control as="textarea" rows={3} name="motivo_consulta" value={formData.motivo_consulta} onChange={handleChange} />
            </Form.Group>
          </div>
        )}

        {step === 2 && (
          <div className="form-step">
            <Form.Group className="mb-3">
              <Form.Label>Evaluación fisioterapéutica</Form.Label>
              <Form.Control as="textarea" rows={4} name="evaluacion" value={formData.evaluacion} onChange={handleChange} />
            </Form.Group>
          </div>
        )}

        {step === 3 && (
          <div className="form-step">
            <Form.Group className="mb-3">
              <Form.Label>Diagnóstico y plan</Form.Label>
              <Form.Control as="textarea" rows={4} name="diagnostico" value={formData.diagnostico} onChange={handleChange} />
            </Form.Group>
          </div>
        )}
      </div>

      <div className="d-flex justify-content-between mt-3">
        {step > 0 && (
          <Button variant="secondary" onClick={prevStep}>Atrás</Button>
        )}
        {step < 3 ? (
          <Button variant="primary" onClick={nextStep}>Siguiente</Button>
        ) : (
          <Button variant="success" onClick={handleSubmit}>Guardar</Button>
        )}
      </div>
    </Card>
  );
};

export default AgendarFicha;
