import React from 'react';
import { Button, Table } from 'react-bootstrap';

const Estudiantes = () => {
  const estudiantes = [
    {
      nombre: 'Andrea',
      apellido: 'López',
      correo: 'andrea.lopez@puce.edu.ec',
      sector: 'Centro Norte',
      semestre: 7,
      ultimaConexion: '2025-06-04 18:23',
      fichas: 5
    },
    {
      nombre: 'Carlos',
      apellido: 'García',
      correo: 'carlos.garcia@puce.edu.ec',
      sector: 'Sur',
      semestre: 6,
      ultimaConexion: '2025-06-05 12:45',
      fichas: 3
    },
    {
      nombre: 'Valeria',
      apellido: 'Mendoza',
      correo: 'valeria.mendoza@puce.edu.ec',
      sector: 'Norte',
      semestre: 8,
      ultimaConexion: '2025-06-06 08:10',
      fichas: 8
    }
  ];

  const handleEditar = (index) => {
    alert(`Editar estudiante: ${estudiantes[index].nombre} ${estudiantes[index].apellido}`);
    // Aquí puedes redirigir o abrir modal en el futuro
  };

  return (
    <div>
      <h4>Estudiantes Registrados</h4>
      <Table striped bordered hover className="mt-3">
        <thead className="table-light">
          <tr>
            <th>#</th>
            <th>Nombre</th>
            <th>Apellido</th>
            <th>Correo</th>
            <th>Sector</th>
            <th>Semestre</th>
            <th>Última conexión</th>
            <th>Fichas recolectadas</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {estudiantes.map((est, index) => (
            <tr key={index}>
              <td>{index + 1}</td>
              <td>{est.nombre}</td>
              <td>{est.apellido}</td>
              <td>{est.correo}</td>
              <td>{est.sector}</td>
              <td>{est.semestre}</td>
              <td>{est.ultimaConexion}</td>
              <td>{est.fichas}</td>
              <td>
                <Button
                  variant="primary"
                  size="sm"
                  onClick={() => handleEditar(index)}
                >
                  Editar
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
};

export default Estudiantes;
