import React, { useRef, useState } from 'react';
import { Form, Button, Row, Col } from 'react-bootstrap';
import './AgendarFicha.css';

const AgendarFicha = () => {
  const [step, setStep] = useState(1);
  const [formData, setFormData] = useState({});
  const containerRef = useRef(null);
  const totalSteps = 4;

  const scrollToTop = () => {
    containerRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const validateStep = () => {
    if (step === 1) {
      return formData.nombre_paciente && formData.apellido_paciente;
    }
    return true;
  };

  const nextStep = () => {
    if (step < totalSteps && validateStep()) {
      setStep(step + 1);
      scrollToTop();
    } else if (!validateStep()) {
      alert('Por favor completa los campos obligatorios.');
    }
  };

  const prevStep = () => {
    if (step > 1) {
      setStep(step - 1);
      scrollToTop();
    }
  };

  const handleSubmit = () => {
    console.log('Datos enviados:', formData);
    alert('Ficha registrada con éxito.');
  };

  return (
    <div className="container mt-4" ref={containerRef}>
      <h4 className="mb-4">Registro de Ficha Clínica</h4>

      <div className="wizard-steps mb-4">
        {[1, 2, 3, 4].map((s) => (
          <div key={s} className={`step-circle ${step === s ? 'active' : step > s ? 'done' : ''}`}>{s}</div>
        ))}
      </div>

      <div className="slide-form-container">
        {/* Paso 1 */}
        <div className={`form-slide ${step === 1 ? 'active' : ''}`}>
          <h5>Paso 1: Datos del Paciente</h5>
          <Form>
            <Row>
              <Col md={6}>
                <Form.Group>
                  <Form.Label>Nombre del paciente *</Form.Label>
                  <Form.Control name="nombre_paciente" value={formData.nombre_paciente || ''} onChange={handleChange} required />
                </Form.Group>
              </Col>
              <Col md={6}>
                <Form.Group>
                  <Form.Label>Apellido del paciente *</Form.Label>
                  <Form.Control name="apellido_paciente" value={formData.apellido_paciente || ''} onChange={handleChange} required />
                </Form.Group>
              </Col>
            </Row>
            <Row className="mt-3">
              <Col md={4}>
                <Form.Group>
                  <Form.Label>Edad</Form.Label>
                  <Form.Control type="number" name="edad" onChange={handleChange} />
                </Form.Group>
              </Col>
              <Col md={4}>
                <Form.Group>
                  <Form.Label>Género</Form.Label>
                  <Form.Select name="genero" onChange={handleChange}>
                    <option value="">Seleccione</option>
                    <option value="Masculino">Masculino</option>
                    <option value="Femenino">Femenino</option>
                  </Form.Select>
                </Form.Group>
              </Col>
              <Col md={4}>
                <Form.Group>
                  <Form.Label>Fecha de nacimiento</Form.Label>
                  <Form.Control type="date" name="fecha_nacimiento" onChange={handleChange} />
                </Form.Group>
              </Col>
            </Row>
            <Row className="mt-3">
              <Col md={6}>
                <Form.Group>
                  <Form.Label>Escuela</Form.Label>
                  <Form.Control name="escuela" onChange={handleChange} />
                </Form.Group>
              </Col>
              <Col md={6}>
                <Form.Group>
                  <Form.Label>Grado</Form.Label>
                  <Form.Control name="grado" onChange={handleChange} />
                </Form.Group>
              </Col>
            </Row>
            <Row className="mt-3">
              <Col md={6}>
                <Form.Group>
                  <Form.Label>Nombre del tutor</Form.Label>
                  <Form.Control name="nombre_tutor" onChange={handleChange} />
                </Form.Group>
              </Col>
              <Col md={6}>
                <Form.Group>
                  <Form.Label>Teléfono de contacto</Form.Label>
                  <Form.Control name="telefono_tutor" onChange={handleChange} />
                </Form.Group>
              </Col>
            </Row>
            <Row className="mt-3">
              <Col>
                <Form.Group>
                  <Form.Label>Correo de contacto</Form.Label>
                  <Form.Control type="email" name="correo_tutor" onChange={handleChange} />
                </Form.Group>
              </Col>
            </Row>
          </Form>
        </div>

        {/* Paso 2 */}
        <div className={`form-slide ${step === 2 ? 'active' : ''}`}>
          <h5>Paso 2: Antecedentes Médicos</h5>
          <Form.Group className="mb-3">
            <Form.Label>Motivo de consulta</Form.Label>
            <Form.Control as="textarea" rows={3} name="motivo_consulta" onChange={handleChange} />
          </Form.Group>
          <Form.Group>
            <Form.Label>Inicio de los síntomas</Form.Label>
            <Form.Select name="inicio_sintomas" onChange={handleChange}>
              <option value="">Seleccione</option>
              <option value="Gradual">Gradual</option>
              <option value="Repentino">Repentino</option>
            </Form.Select>
          </Form.Group>
        </div>

        {/* Paso 3 */}
        <div className={`form-slide ${step === 3 ? 'active' : ''}`}>
          <h5>Paso 3: Evaluación Fisioterapéutica</h5>
          <Form.Group>
            <Form.Label>Evaluación de la postura</Form.Label>
            <Form.Control as="textarea" rows={2} name="evaluacion_postura" onChange={handleChange} />
          </Form.Group>
        </div>

        {/* Paso 4 */}
        <div className={`form-slide ${step === 4 ? 'active' : ''}`}>
          <h5>Paso 4: Diagnóstico y Plan de Intervención</h5>
          <Form.Group>
            <Form.Label>Diagnóstico fisioterapéutico</Form.Label>
            <Form.Control as="textarea" rows={2} name="diagnostico_fisioterapeutico" onChange={handleChange} />
          </Form.Group>
        </div>
      </div>

      <div className="d-flex justify-content-between mt-4">
        <Button onClick={prevStep} disabled={step === 1}>Anterior</Button>
        {step < totalSteps ? (
          <Button onClick={nextStep}>Siguiente</Button>
        ) : (
          <Button variant="success" onClick={handleSubmit}>Finalizar</Button>
        )}
      </div>
    </div>
  );
};

export default AgendarFicha;
